-- Component SQ_PORTPOLS, Type SOURCE 
CREATE OR REPLACE TEMPORARY TABLE SQ_PORTPOLS AS
(
SELECT /* adding column aliases to ensure proper downstream column references */
$1 as SC_POLICY_NUM,
$2 as CHG_EFF_DT,
$3 as source_record_id
FROM (
SELECT SRC.*, row_number() over (order by 1) AS source_record_id FROM (
SELECT PORTPOLS.SC_POLICY_NUM ,PORTPOLS.CHG_EFF_DT 
FROM
 DB_T_CORE_MEMBXREF_PROD.PORTPOLS WHERE CHG_EXP_DT = CAST(''9999-12-31'' AS DATE )   
) SRC
)
);


-- Component exp_ASSIGN_DUMMY_TG, Type EXPRESSION 
CREATE OR REPLACE TEMPORARY TABLE exp_ASSIGN_DUMMY_TG AS
(
SELECT
SQ_PORTPOLS.SC_POLICY_NUM as SC_POLICY_NUM,
SQ_PORTPOLS.CHG_EFF_DT as CHG_EFF_DT,
''PORTPOLS'' as var_TABLENAME,
LKP_1.FEED_DT /* replaced lookup LKP_STG_MEMBXREF_TRIGGER */ as var_FEED_DT,
var_FEED_DT as out_FEED_DT,
SQ_PORTPOLS.source_record_id,
row_number() over (partition by SQ_PORTPOLS.source_record_id order by SQ_PORTPOLS.source_record_id) as RNK
FROM
SQ_PORTPOLS
LEFT JOIN LKP_STG_MEMBXREF_TRIGGER LKP_1 ON LKP_1.TABLENAME = var_TABLENAME
QUALIFY RNK = 1
);


-- Component LKP_STG_PORTPOLS, Type LOOKUP 
CREATE OR REPLACE TEMPORARY TABLE LKP_STG_PORTPOLS AS
(
SELECT
LKP.SC_POLICY_NUM,
exp_ASSIGN_DUMMY_TG.source_record_id,
ROW_NUMBER() OVER(PARTITION BY exp_ASSIGN_DUMMY_TG.source_record_id ORDER BY LKP.SC_POLICY_NUM asc) RNK
FROM
exp_ASSIGN_DUMMY_TG
LEFT JOIN (
SELECT LTRIM(RTRIM(STG_PORTPOLS.SC_POLICY_NUM)) as SC_POLICY_NUM FROM DB_T_STAG_MEMBXREF_PROD.STG_PORTPOLS WHERE
 STG_PORTPOLS. LOAD_DT=(SELECT FEED_DT FROM DB_T_STAG_MEMBXREF_PROD.STG_MEMBXREF_TRIGGER WHERE TABLENAME=''PORTPOLS'')
) LKP ON LKP.SC_POLICY_NUM = exp_ASSIGN_DUMMY_TG.SC_POLICY_NUM
