-- Component PORTPOLS_UPD_EXPIRED_POLS, Type TARGET 
INSERT INTO DB_T_CORE_MEMBXREF_PROD.PORTPOLS
(
SC_POLICY_NUM,
CHG_EFF_DT,
CHG_EXP_DT,
ECTL_BATCH_ID,
ECTL_MODIFY_TS,
ECTL_TX_CD,
ECTL_END_DATE
)
SELECT
upd_PORTPOLS_EXPIRED_POLS.SC_POLICY_NUM as SC_POLICY_NUM,
upd_PORTPOLS_EXPIRED_POLS.CHG_EFF_DT as CHG_EFF_DT,
