upd_PORTPOLS_EXPIRED_POLS.ECTL_END_DATE as ECTL_END_DATE
FROM
upd_PORTPOLS_EXPIRED_POLS
WHERE UPDATE_STRATEGY_ACTION = 0;

/* Perform Updates */
MERGE INTO DB_T_CORE_MEMBXREF_PROD.PORTPOLS
USING upd_PORTPOLS_EXPIRED_POLS ON (UPDATE_STRATEGY_ACTION = 1 and  DB_T_CORE_MEMBXREF_PROD.PORTPOLS.SC_POLICY_NUM = upd_PORTPOLS_EXPIRED_POLS.SC_POLICY_NUM and DB_T_CORE_MEMBXREF_PROD.PORTPOLS.CHG_EFF_DT = upd_PORTPOLS_EXPIRED_POLS.CHG_EFF_DT )
WHEN MATCHED THEN UPDATE
SET
SC_POLICY_NUM = upd_PORTPOLS_EXPIRED_POLS.SC_POLICY_NUM,
CHG_EFF_DT = upd_PORTPOLS_EXPIRED_POLS.CHG_EFF_DT,
CHG_EXP_DT = upd_PORTPOLS_EXPIRED_POLS.out_CHG_EXP_DT_new,
ECTL_BATCH_ID = upd_PORTPOLS_EXPIRED_POLS.out_ECTL_BATCH_ID,
ECTL_MODIFY_TS = upd_PORTPOLS_EXPIRED_POLS.out_ORIG_INS_TS,
ECTL_TX_CD = upd_PORTPOLS_EXPIRED_POLS.ECTL_TX_CD,
ECTL_END_DATE = upd_PORTPOLS_EXPIRED_POLS.ECTL_END_DATE
;


